import React from 'react'

const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  return (
    <div>
      <Header 
      props={course} 
      />
      <Content props={parts}
      />
      <Total props={parts}
      />
    </div>
  )
}

const Header = (props) => {
  return (
    <h1>{props}</h1>
  )
  }

const Content = (props) => {
  return (
    <div>
    <Part part={props[0].name} exercises={props[0].exercises} />
    <Part part={props[1].name} exercises={props[1].exercises} />
    <Part part={props[2].name} exercises={props[2].exercises} />
  </div>
  )
}



const Total = (props) => {
  return (
    <div>
    <p>Number of exercises {props[0].exercises + props[1].exercises + props[2].exercises}</p>
  </div>
  )
}

const Part = ({ part, exercises }) => (
  <p>
      {part} {exercises}
  </p>
)

export default App
