import React from 'react'

const App = () => {
  const courses = [
    {
      name: 'Half Stack application development',
      id: 1,
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        },
        {
          name: 'Redux',
          exercises: 11,
          id: 4
        }
      ]
    }, 
    {
      name: 'Node.js',
      id: 2,
      parts: [
        {
          name: 'Routing',
          exercises: 3,
          id: 1
        },
        {
          name: 'Middlewares',
          exercises: 7,
          id: 2
        }
      ]
    }
  ]


  //const total = course.parts.reduce((acc, cur) => acc = cur.id)
  // console.log(total);
  return (
    <div>
      <Course course={courses[0]}/>
    </div>
  )
}

const Course = ({course}) => (
  <div>
    <Header course={course.name} />
    <Content parts={course.parts} />
    <Total total={course.parts} />
  </div>

)

const Header = (props) => (
    <h1>{props.course}</h1>
)


const Content = (props) => (
  <div>
    {props.parts.map(function(part) {
      return <Part part={part.name} exercises={part.exercises} />
    })}
  </div>
)

const Total = (props) => (
    <div>

    <p><b>Total of {props.total.reduce((acc, cur) => acc + cur.exercises, 0)} exercises</b></p>
  </div>
)


const Part = (props) => (
  <p>
      {props.part} {props.exercises}
  </p>
)

export default App

