// 6.10 anekdootit, step8
const notificationReducer = (state = 'This is a notification', action) => {
    switch (action.type) {
        case 'NOTIFICATION':
            return action.notification
        default:
            return state
    }
}

export const notificationFade = (notification) => {
    return { type: 'NOTIFICATION', 
    notification: notification, 
    style: {
        border: 'solid',
        padding: 10,
        borderWidth: 1
      } 
    }
}

export const notificationVote = (id) => {
    return { type: 'NOTIFICATION_VOTE', id: id }
}

export default notificationReducer
