// 6.10 anekdootit, step8
const notificationReducer = (state = 'This is a notification', action) => {
    switch (action.type) {
        case 'NOTIFICATION_VOTE':
            return action.notification
        case 'NOTIFICATION_FADE':
            return action.notification
        default:
            return state
    }
}

export const notificationFade = () => {
    return { type: 'NOTIFICATION_FADE', 
    notification: { 
        content: 'test', 
        style: {
            border: 'solid',
            padding: 10,
            borderWidth: 1
      }  
    }
  }
}

export const notificationVote = (content) => {
    return { type: 'NOTIFICATION_VOTE',
    notification: { 
        content: content, 
        style: {
            border: 'solid',
            padding: 10,
            borderWidth: 1
      }  
    }  
  }
}

export default notificationReducer
