// 6.10 anekdootit, step8
const notificationReducer = (state = 'This is a notification', action) => {
    switch (action.type) {
        case 'NOTIFICATION_SET':
            return action.notification
        case 'NOTIFICATION_FADE':
            return action.notification
        default:
            return state
    }
}

export const notificationSet = (content, time) => {
    return dispatch => {
        dispatch({
            type: 'NOTIFICATION_SET',
            notification: { 
                content: `${content}`,
                visible: true
            }
        })
        setTimeout(() => {
            dispatch(notificationFade())
        }, (parseInt(time)))
    }
}

export const notificationFade = (time) => {
    return dispatch => {
        setTimeout(() => {
            dispatch({
                type: 'NOTIFICATION_FADE',
                notification: {
                    visible: false
                }
            })
        }, time)
    /*return { type: 'NOTIFICATION_FADE',
    notification: {
        visible: false
    }*/    
}
}

export default notificationReducer
