import { useState } from "react";
import { newAnecdote } from '../reducers/anecdoteReducer'
import { notificationSet, notificationFade } from "../reducers/notificationReducer";
import { useDispatch } from "react-redux";
import anecdoteService from '../services/anecdotes'


// 6.7: anekdootit, step5
const AnecdoteForm = () => {

    const [input, setInput] = useState('')
    const dispatch = useDispatch()



    // 6.4: anekdootit, step2
    const createAnecdote = async (e) => {
        e.preventDefault()
        let response = await anecdoteService.createNew(input)
        dispatch(newAnecdote(response))
        dispatch(notificationSet(`You created ${input}`))
        setTimeout(() => {
            dispatch(notificationFade())
        }, 5000)
    }

    return (
    <div>
      <h2>create new</h2>
      <form onSubmit={createAnecdote}>
        <div>
          <input type="text" value={input} onChange={(e) => {setInput(e.target.value)}}/>
        </div>
        <input type="submit" value="create" />
      </form>
    </div>
    )
}

export default AnecdoteForm
