// 6.10 anekdootit, step8
const notificationReducer = (state = 'This is a notification', action) => {
    switch (action.type) {
        case 'NOTIFICATION_SET':
            return action.notification
        case 'NOTIFICATION_FADE':
            return action.notification
        default:
            return state
    }
}


export const notificationOn = (content, time) => {
    notificationSet(content)
    setTimeout(() => {
        notificationFade()
    }, time)
}

const notificationSet = (content) => {
    return dispatch => {
        dispatch({
        type: 'NOTIFICATION_SET',
        notification: { 
            content: `${content}`,
            visible: true
        }
    })
}
}
const notificationFade = () => {      
    return dispatch => {
        dispatch({
        type: 'NOTIFICATION_FADE',
            notification: {
            visible: false
            }
        })  
}
}


export default notificationReducer
