          </div>
          <div>
            author
            <input name='author' value={author} onChange={onChange} />
          </div>
          <div>
            url for more info
            <input name='info' value={info} onChange={onChange} />
          </div>
          <button>create</button>
        </form>
      </div>
    )
  }

export default CreateNew
