import AnecdoteList from "./AnecdoteList";
import CreateNew from "./CreateNew";

const Content = () => {

    const [page, setPage] = useState('anecdotes')

    if (page === 'anecdotes') {
        return <AnecdoteList />
    } else if (page === 'new') {
        return <CreateNew />
    } else if (page === 'about') {
        return <About />
    }

}

export default Content
