import { useState } from 'react'

export const useField = (initialState = {}) => {
  const [formContent, setFormContent] = useState(initialState)

  const onChange = (e) => {
    setFormContent({ ...FormData, [e.target.name]: e.target.value })
  }

  const handleSubmit = (e) => {
      e.preventDefault()
      onsubmit?.(formContent)
  }

  return {
    formContent,
    onChange,
    handleSubmit
  }
}



